class Solution {

   int hcf(int a,int b){
       if(a==0 ){
           return b;
       }else if(b==0){
           return a;
       }
       if(a>b)
       return hcf(a%b,b);
       return hcf(a,b%a);
   } 
public:

    string gcdOfStrings(string str1, string str2) {
        //finding divisor length i.e hcf of both string lengths
        int n1 = str1.size();
        int n2 = str2.size();
        int wind = hcf(n1,n2);
        int flag=0;
        string init = str1.substr(0,wind);
        int i=0;
        while(i<n1 && i<n2){
            string s1 = str1.substr(i,wind);
            string s2 = str2.substr(i,wind);
            if(s1 != s2 ||
             s1 != init ||
              s2!=  init){
                flag=1;
                break;
            }
            i+=wind;
        }
        
            while(i<n1){
                if(str1.substr(i,wind) != init){
                    flag=1;
                    break;
                }
                i+=wind;
            }
        
       
            while(i<n2){
                if(str2.substr(i,wind) != init){
                    flag=1;
                    break;
                }
                i+=wind;
            }
        
        if(flag==1)return "";
        return init;
    }
};

