class Solution {

public:

    vector<int> twoSum(const vector<int>& numbers, int target) {
        for (int i = 0; i < numbers.size(); i++) {

            int j = (int)(upper_bound(numbers.begin(), numbers.end(), target - numbers[i]) - numbers.begin()) - 1;

            // invariate: i < j
            if (numbers[j] == target - numbers[i]) { return { i + 1, j + 1 }; }

        }

        return {};

    }

};

