using UnityEngine;
using UnityEngine.PSVita;

public class MovieManager : MonoBehaviour
{
    public RenderTexture vidTex;
    public bool isPlaying;

	public int movieCount = 0;
	public string[] movies = { "StreamingAssets\\Movie01.mp4", "StreamingAssets\\Movie02.mp4", "StreamingAssets\\Movie03.mp4" };

	void Start() {
		// Activate the movie. Don't init the VideoPlayer just yet.
		ActivateMovie();
	}

	void ActivateMovie() {
		// Let's activate the movie player and then play it.
		PSVitaVideoPlayer.Init(vidTex);
		PSVitaVideoPlayer.Play(movies[movieCount], PSVitaVideoPlayer.Looping.None, PSVitaVideoPlayer.Mode.RenderToTexture);
	}

