class Employee {
    private final String name;
    private final int salary;

    public Employee(final String name, final int salary) {
        this.name = name;
        this.salary = salary;
    }

    @Override
    public boolean equals(Object o) {
        if (o instanceof Employee) {
            Employee e = (Employee)o;
            return e.name.equals(name) && e.salary == salary;
        }
        return false;
    }

    @Override
    public String toString() {
        return "Emp " + name + " - " + salary;
    }

    public int getSalary() {
        return salary;
    }
}

class Company {
    private final String name;
    private final int budget;
    private final Employee[] employees;
    private int currLen;
    private final static int MAX_LEN = 30;

    public Company(String name, int budget) {
        this.name = name;
        this.budget = budget;
        employees = new Employee[MAX_LEN];
    }

    public boolean add(Employee employee) {
        if (MAX_LEN == currLen) {
            return false;
        }
        for (int i = 0; i < currLen; i++) {
            if (employees[i].equals(employee)) {
                return false;
            }
        }
        employees[currLen++] = employee;
        return true;
    }

    @Override
    public String toString() {
        String s = name;
        for (int i = 0; i < currLen; i++) {
            s += " " + employees[i];
        }
        return s;
    }

    public int pay() {
        int sum = 0;
        for (int i = 0; i < currLen; i++) {
            sum += employees[i].getSalary();
        }
        return budget - sum;
    }
}

public class Main {
    public static void main(String[] args) {
        Company c = new Company("intel", 100);
