struct Spy {
        explicit Spy(bool& is_deleted, int& copy_count)
                : is_deleted_(is_deleted), copy_count_(copy_count) {}

        Spy(const Spy& rhs)
                : is_deleted_(rhs.is_deleted_), copy_count_(rhs.copy_count_) {
            ++copy_count_;
        }
        ~Spy() {
            is_deleted_ = true;
        }

        bool& is_deleted_;
        int& copy_count_;
    };
void NullptrAssignment() {
        {
            UniquePtr<Spy> uptr;
            uptr = nullptr;

            ASSERT_EQUAL(uptr.Get(), nullptr);
        } {
            int copy_count_ = 0;
            bool is_deleted = false;
            auto *ptr = new Spy(is_deleted, copy_count_);
            UniquePtr<Spy> uptr(ptr);
            uptr = nullptr;

            ASSERT_EQUAL(uptr.Get(), nullptr);
            ASSERT(is_deleted);
        }
    }

void ResetTest() {
        bool is_deleted = false;
        bool is_deleted2 = false;
        int copy_count_ = 0;
        int copy_count_2 = 0;

        {
            auto *ptr = new Spy(is_deleted, copy_count_);
            auto *ptr2 = new Spy(is_deleted2, copy_count_2);

            UniquePtr <Spy> uptr(ptr);
            ASSERT_EQUAL(uptr.Get(), ptr);

            uptr.Reset(ptr2);

            ASSERT_EQUAL(uptr.Get(), ptr2);
            ASSERT_EQUAL(is_deleted);
            ASSERT_EQUAL(!is_deleted2);

        }
        ASSERT_EQUAL(is_deleted2);
        ASSERT_EQUAL(copy_count_, 0)
        ASSERT_EQUAL(copy_count_2, 0)
    }

