void display(int port)
{
	char on = 'X';
	char off = ' ';
	system("cls");
	printf("8 bit ODR Port Emulation\n\n");
	printf("\n");
	for (int i = 0; i < 8; i++)
	{
		printf(" %-2d", 7 - i);
	}
	printf("\n");
	for (int i = 7; i >= 0; --i)
	{
		printf("[%c]", (port & (1 << i)) ? on : off);
	}
	printf("\n\n");
}

int main()
{
	/*Create a variable, unsigned char, which will act as the register for our port. Set this to an initial binary value of
	0b00000000, which is zero in decimal*/
	unsigned char port = 0b00000000;

	/*Create a variable to store user input, perhaps a char will be appropriate.*/
	char input = "y";

	/*Using a combination of for loops and printf with appropriate formatting, create eight, numbered, boxes on
	screen using the ‘[‘ and ‘]’ characters*/
	while (input != 'x' || input != 'X')
	{		
		display(port);
		/*Prompt the user and ask for input between 0-7 or alternatively the letter ‘x’ to end the program.*/
		printf("Enter a number between 0 and 7 or 'x' to exit: ");
		scanf(" %c", &input);
		if (input == 'x' || input == 'X')
