def f(s, m):
    if 36 <= s <= 60:
        return m % 2 == 0
    if s > 60:
        return m % 2 != 0
    if m == 0:
        return 0
    h = [f(s+1, m-1), f(s*2, m-1), f(s*3, m-1)]
    return any(h) if (m - 1) % 2 == 0 else all(h)
