function armies(arr) {
    let battle = {};

    for (let command of arr) {

        if (command.includes('arrives')) {
            let leader = command.split(' arrives')[0];

            if (!battle.hasOwnProperty(leader)) {
                battle[leader] = {};
            }

        } else if (command.includes(':')) {
            let [leader, army] = command.split(': ');
            let [armyName, armyCount] = army.split(', ');
            armyCount = Number(armyCount);

            if (battle.hasOwnProperty(leader)) {
                if (!battle[leader][armyName]) {
                    battle[leader][armyName] = 0;
                }
                battle[leader][armyName] += armyCount;
            }

        } else if (command.includes(' + ')) {
            let [armyName, armyCount] = command.split(' + ');

            for (let leader in battle) {
                if (battle[leader].hasOwnProperty(armyName)) {
                    battle[leader][armyName] += Number(armyCount);
                }
            }

        } else if (command.includes('defeated')) {
            let leader = command.split(' defeated')[0];
            if (battle.hasOwnProperty(leader)) {
                delete battle[leader];
            }
        }
    }

    let sorted = Object.entries(battle);

    for (let tokens of sorted) {
        let leader = tokens.shift();
        let estimate = Object.values(tokens).reduce((s, n) => s + n);

        console.log(`${leader}: ${estimate}`);

        
    }

}

