vector<int> seg_tree, a, res;

void build(int v, int tl, int tr) {
    if (tr - tl == 1) {
        seg_tree[v] = a[tl];
        return;
    }
    int tm = tl + (tr - tl) / 2;
    build(2 * v + 1, tl, tm);
    build(2 * v + 2, tm, tr);
}
int get(int v, int tl, int tr, int pos) {
    if (tr - tl == 1) {
        return seg_tree[v];
    }
    int tm = (tl + tr) / 2;
    if (pos < tm) {
        return seg_tree[v] + get(2 * v + 1, tl, tm, pos);
    } else {
        return seg_tree[v] + get(2 * v + 2, tm, tr, pos);
    }
}
void update(int v, int tl, int tr, int l, int r, int x) {
    if (l >= r || tl >= tr) {
        return;
    }
    cerr << v << ' ' << tl << ' ' << tr << ' ' << l << ' ' << r << '\n';
    if (l <= tl && tr <= r) {
        seg_tree[v] += x;
        a[tl] += x;
        return;
    }
    int m = tl + (tr - tl) / 2;
    update(2 * v + 1, tl, m, l, min(r, m), x);
    update(2 * v + 2, m, tr, max(l, m), r, x);
}
inline void solve() {
    int n, q;
    cin >> n;
    a.resize(n);
    res.resize(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
        res[i] = a[i];
    }
    seg_tree.resize(4 * (n + 1));
    build(0, 0, n);
    cin >> q;
    while (q-- > 0) {
        char s;
        cin >> s;
        if (s == 'g') {
            int l;
            cin >> l;
            l--;
            if (res[l] == -1) {
                res[l] = get(0, 0, n, l);
            }
            cout << get(0, 0, n, l) << '\n';
        } else {
            int l, r, val;
            cin >> l >> r >> val;
            l--;
            update(0, 0, n, l,  r, val);
        }
    }
    for (auto& i : a) {
        cout << i << ' ';
    }
}
signed main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    cout.tie(nullptr);
    int q = 1;
    //cin >> q;
    while (q-- > 0) {
        solve();
    }
}

