#define MAX_QUERY_STRING					3072
#define MAX_WHERE_STRING					128
#define MAX_TABLE_NAME						50
#define MAX_COLUMN_NAME						50
#define MAX_COLUMN_VALUE_STRING				256
#define MAX_COLUMN_VALUE_INT				20
