    public class Bulb{
        boolean isArrowBulb = false;
        boolean isNormalBulb = false;
        int colorID = 0;
        boolean flash = false;
        int arrow = 0;

        /**
         * Create a default param bulb
         */
        public Bulb(){
            isNormalBulb = true;
            isArrowBulb = false;
            colorID = 2;
            flash = true;
            arrow = 0;
        }

        /**
         * Create a new bulb with 5 params
         * @param normal Is this bulb a normal (fully filled) bulb?
         * @param arr Does this bulb have an arrow shape (replaces 'normal')?
         * @param clrid The color of the bulb, red -> 2, yellow -> 1, and green -> 0
         * @param fls Does this bulb flash?
         * @param arrty The bulb's arrow type, left -> 3, right -> 2, up -> 1, down -> 0
         */
        public Bulb(boolean normal, boolean arr, int clrid, boolean fls, int arrty){
            isNormalBulb = normal;
            isArrowBulb = arr;
            colorID = clrid;
            flash = fls;
            arrow = arrty;
        }

        public void setArrow(int arrow) {
            this.arrow = arrow;
        }

        public void setArrowBulb(boolean arrowBulb) {
            isArrowBulb = arrowBulb;
        }

        public void setColorID(int colorID) {
            this.colorID = colorID;
        }

        public void setFlash(boolean flash) {
            this.flash = flash;
        }

        public void setNormalBulb(boolean normalBulb) {
            isNormalBulb = normalBulb;
        }

        public String convertToString(){
            String saveStr = "";
            String separatorCallChar = "H";
            // e.g. trueHtrueH1HtrueH2
            saveStr = isNormalBulb + separatorCallChar + isArrowBulb + separatorCallChar + colorID + separatorCallChar + flash + separatorCallChar + arrow;

            return saveStr;
        }
    }
}

class Solution:
    def climbStairs(self, n: int) -> int:

        # better way of dealing with base case
        if n == 1:
            return 1
        if n == 2:
            return 2

        dp = [0]*n
        dp[0] = 1
        dp[1] = 2

        for i in range(2, n):
            dp[i] = dp[i-1] + dp[i-2]

        return dp[-1]

class Solution:
    def climbStairs(self, n: int) -> int:

        if n == 1:
            return 1
        if n == 2:
            return 2

        n0 = 1
        n1 = 2

        for i in range(2, n):
            count = n0 + n1
            n0 = n1
            n1 = count

        return count

