#include <iostream>

using namespace std;

int main() {
	setlocale(LC_ALL, "Russian");

	int n = 0;
	cout << "Введите размерность квадратной матрицы n: ";
	cin >> n;

	if (n <= 0) {
		cout << "Размерность матрицы должна быть больше 0." << endl;
		return 1;
	}

	int** matrix = new int* [n];
	for (int i = 0; i < n; i++) {
		matrix[i] = new int[n];
	}

	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			cout << "Элемент [" << i << "][" << j << "]: ";
			cin >> matrix[i][j];
		}
	}

	cout << "Введенная матрица:" << endl;
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			cout << matrix[i][j] << "\t";
		}
		cout << endl;
	}

	int check_trigger = 1; // Изначально предполагаем, что матрица является магическим квадратом
	int sum_check = 0;
	for (int j = 0; j < n; j++) { // Используем первую строку для инициализации sum_check
		sum_check += matrix[0][j];
	}

	// Проверка сумм строк
	for (int i = 1; i < n; i++) {
		int sum_row = 0;
		for (int j = 0; j < n; j++) {
			sum_row += matrix[i][j];
		}
		if (sum_row != sum_check) {
			check_trigger = 0;
			break;
		}
	}

	// Проверка сумм столбцов
	if (check_trigger) { // Проверяем только если строки прошли проверку
		for (int j = 0; j < n; j++) {
			int sum_col = 0;
			for (int i = 0; i < n; i++) {
				sum_col += matrix[i][j];
			}
			if (sum_col != sum_check) {
				check_trigger = 0;
				break;
			}
		}
	}

	// Проверка сумм диагоналей
	if (check_trigger) { // Проверяем только если строки и столбцы прошли проверку
		int sum_diag1 = 0, sum_diag2 = 0;
		for (int i = 0; i < n; i++) {
			sum_diag1 += matrix[i][i];
			sum_diag2 += matrix[i][n - 1 - i];
		}
		if (sum_diag1 != sum_check || sum_diag2 != sum_check) {
			check_trigger = 0;
		}
	}

	// Освобождение выделенной памяти
	for (int i = 0; i < n; i++) {
		delete[] matrix[i];
	}
	delete[] matrix;

	if (check_trigger == 1) {
		printf("Матрица - магический квадрат");
	}
	else
	{
		printf("Матрица - не магический квадрат");
	}
	return 0;
}

