from abc import ABC, abstractmethod


class Department(ABC):
    @abstractmethod
    def get_department_name(self):
        pass

    @abstractmethod
    def get_head_of_department(self):
        pass


class CSE(Department):
    def __init__(self, num_students):
        self.num_students = num_students

    def get_department_name(self):
        return "CSE Department"

    def get_head_of_department(self):
        return "Umme Sara"


class EEE(Department):
    def __init__(self, num_students):
        self.num_students = num_students

    def get_department_name(self):
        return "EEE Department"

    def get_head_of_department(self):
        return "Mamun Ur Rashid"


class TE(Department):
    def __init__(self, num_students):
        self.num_students = num_students

    def get_department_name(self):
        return "TEX Department"

    def get_head_of_department(self):
        return "Mahabub Hasan"


class Level(ABC):
    @abstractmethod
    def get_level_name(self):
        pass


class L1(Level):
    def get_level_name(self):
        return "L 1"


class L2(Level):
    def get_level_name(self):
        return "L 2"


class L3(Level):
    def get_level_name(self):
        return "L 3"


class Section(ABC):
    @abstractmethod
    def get_section_name(self):
        pass

    @abstractmethod
    def get_num_students(self):
        pass


class SectionA(Section):
    def __init__(self, num_students):
        self.num_students = num_students

    def get_section_name(self):
        return "S A"

    def get_num_students(self):
        return self.num_students


class SectionB(Section):
    def __init__(self, num_students):
        self.num_students = num_students

    def get_section_name(self):
        return "S B"

    def get_num_students(self):
        return self.num_students


class DepartmentFactory(ABC):
    @abstractmethod
    def create_department(self):
        pass


class CSEDepartmentFactory(DepartmentFactory):
    def create_department(self):
        cse_department = CSE(num_students=120)
        l1 = L1()
        l2 = L2()
        section_a_l1 = SectionA(num_students=50)
        section_b_l1 = SectionB(num_students=70)
        section_a_l2 = SectionA(num_students=60)
        section_b_l2 = SectionB(num_students=80)
        cse_department.levels = [(l1, [section_a_l1, section_b_l1]), (l2, [section_a_l2, section_b_l2])]
        return cse_department


class EEEDepartmentFactory(DepartmentFactory):
    def create_department(self):
        eee_department = EEE(num_students=120)
        l1 = L1()
