class List
{
    public:
    class Node
    {
        int data;
        Node* prev;
        Node* next;
    };
    
    Node* tail;
    Node* head;
    
    List() : head(nullptr), tail(nullptr) {}
    
    Node* push_front(int data)
    {
        Node* newNode = new Node;
        newNode->data = data;
        newNode->prev = nullptr;
        newNode->next = head;
        if (head == nullptr)
        {
            head = newNode;
            tail = newNode;
        }
        else
        {
            head->prev = newNode;
            head = newNode;
        }
        return newNode;
    }
    
    Node* push_back(int data)
    {
        Node* newNode = new Node;
        newNode->data = data;
        newNode->prev = tail;
        newNode->next = nullptr;
        if (tail == nullptr)
        {
            tail = newNode;
            head = newNode;
        }
        else
        {
            tail->next = newNode;
            tail = newNode;
        }
        return newNode;
    }
    
    Node* insert(int index, int data)
    {
        if (index == 0)
        {
            return push_front(data);
        }
        
        Node* iterator = head;
        for (int i = 0; i < index; i++)
        {
            if (iterator->next == nullptr && index > i + 1)
            {
                 std::cout << "Out of scope" << std::endl;
                 return nullptr;
            }
            else if (iterator->next == nullptr && index == i + 1)
            {
                return push_back(data);
            }
            else
            {
                 iterator = iterator->next;
            }
        }
        
        Node* newNode = new Node;
        newNode->data = data;
        
        iterator->prev->next = newNode;
        newNode->prev = iterator->prev;
        newNode->next = iterator;
        iterator->prev = newNode;
    }
    
    
};

