#include <iostream>
#include <thread>
#include <vector>
#include <algorithm>

void quicksort(std::vector<int>& arr, int left, int right) {
    if (left >= right) {
        return;
    }
    
    int pivot = arr[(left + right) / 2];
    int i = left;
    int j = right;
    
    while (i <= j) {
        while (arr[i] < pivot) {
            i++;
        }
        while (arr[j] > pivot) {
            j--;
        }
        if (i <= j) {
            std::swap(arr[i], arr[j]);
            i++;
            j--;
        }
    }
    
    std::thread t1(quicksort, std::ref(arr), left, j);
    std::thread t2(quicksort, std::ref(arr), i, right);
    
    t1.join();
    t2.join();
}

int main() {
    std::vector<int> arr = {9, 3, 7, 1, 5, 8, 2, 6, 4};
    
    quicksort(arr, 0, arr.size() - 1);
    
    std::cout << "Sorted array: ";
    for (int num : arr) {
        std::cout << num << " ";
    }
    std::cout << std::endl;
    
    return 0;
}

#include <iostream>
#include <thread>
#include <vector>

void fibonacci(int n, int& result) {
    if (n <= 1) {
        result = n;
    } else {
        int a, b;
        std::thread t1(fibonacci, n - 1, std::ref(a));
        std::thread t2(fibonacci, n - 2, std::ref(b));
        t1.join();
        t2.join();
        result = a + b;
    }
}

int main() {
    int n = 10; // Calculate Fibonacci sequence up to the 10th number
    int result;
    
    fibonacci(n, result);
    
    std::cout << "Fibonacci sequence up to " << n << "th number is: " << result << std::endl;
    
    return 0;
}

