f64 frand(){
  u32 value = (rand()<<15) | rand();
  return (f64)value/(KIT_U32_MAX>>2);
}



f32 frandf(){
  u32 value = (rand()<<15) | rand();
  return (f32)value/(KIT_U32_MAX>>2);
}




f64 frandRange(f64 start, f64 maxDeviation){
  return start + (frand()*2.0-1.0)*maxDeviation;
}

