using System.Management;
using System.Windows;
using System.Diagnostics;

namespace Example
{
    class getProfile
    {
        public static string[] profileBaseArr = new string [6];
        private static string FormatBytes(String bytesStr)
        {
            long bytes = long.Parse(bytesStr);
            string[] Suffix = { "B", "KB", "MB", "GB", "TB" };
            int i;
            double dblSByte = bytes;
            for (i = 0; i < Suffix.Length && bytes >= 1024; i++, bytes /= 1024)
            {
                dblSByte = bytes / 1024.0;
            }

            return String.Format("{0:0.##}", dblSByte);
        }

        public static string RunCommand(string command)
        {
            ProcessStartInfo startInfo = new ProcessStartInfo
            {
                FileName = "cmd.exe",
                Arguments = $"/c {command}",
                RedirectStandardOutput = true,
                RedirectStandardError = true,
                UseShellExecute = false,
                CreateNoWindow = true,
            };

            using (Process process = new Process { StartInfo = startInfo })
            {
                process.Start();

                string output = process.StandardOutput.ReadToEnd();
                string error = process.StandardError.ReadToEnd();
                process.WaitForExit();

                if (!string.IsNullOrEmpty(error))
                {
                    return $"Error: {error}";
                }

                return output;
            }
        }

        private static string ConvertRamType(int type)
        {
            string outValue = string.Empty;

            switch (type)
            {
                case 0: outValue = "Unknown"; break;
                case 24: outValue = "DDR3"; break;
                case 26: outValue = "DDR4"; break;
                case 34: outValue = "DDR5"; break;
                default: outValue = "Undefined"; break;
            }

            return outValue;
        }

    public static bool IsBetween(char c, char minInclusive, char maxInclusive)
        {
            return c >= minInclusive && c <= maxInclusive;
        }

        public static string ExtractNumbersUsingLinq(string inputString)
        {
            return string.Join(",", new string(inputString
                  .Where(c => IsBetween(c, '0', '9') || c == '.' || c == '-' || char.IsWhiteSpace(c))
                  .ToArray()).Split((char[]?)null, StringSplitOptions.RemoveEmptyEntries));
        }

        public static void UpdateBaseProfileArray()
        {
            ManagementObjectSearcher searcher;
            var LogWindow = MainWindow.LogWindow;
            searcher = new ManagementObjectSearcher(
            "select * from " + "Win32_Processor");
            foreach (ManagementObject mo in searcher.Get())
            {
                //cpu
                profileBaseArr[0] = mo["Name"].ToString();

            }
            searcher = new ManagementObjectSearcher(
            "select * from " + "Win32_VideoController");
            foreach (ManagementObject mo in searcher.Get())
            {
                //gpu
                profileBaseArr[1] = mo["Caption"].ToString();

            }
            searcher = new ManagementObjectSearcher(
            "select * from " + "Win32_PhysicalMemory");
            foreach (ManagementObject mo in searcher.Get())
            {
                //ram
                profileBaseArr[2] = FormatBytes(mo["Capacity"].ToString());
                profileBaseArr[3] = mo["Speed"].ToString();
                //profileBaseArr[4] = mo["MemoryType"].ToString();

            }
            searcher = new ManagementObjectSearcher(
            "select * from " + "Win32_DiskDrive");
            foreach (ManagementObject mo in searcher.Get())
            {
                //main drive\
                profileBaseArr[5] = mo["Caption"].ToString();
                break;

            }
            string cmd = "wmic memorychip get SMBIOSMemoryType";
            string output = RunCommand(cmd);
            profileBaseArr[4] = ConvertRamType(Convert.ToInt32(ExtractNumbersUsingLinq(output)));
        }
    }
}

