template<typename T>
struct lca_tree {
    int N, L;
    vt<int> par, depth;
    vt<T> val;
    vt<vt<int> > dp_par; // dp_par[0] is itself 
    vt<vt<T> > dp_val;
    int timer;
    vt<int> in, out;

    lca_tree() {}

    lca_tree(int N) {
        this->N      = N;
        this->L      = log2(N) + 2;
        this->par    = vt<int> (N, -1);
        this->val    = vt<T> (N);
        this->depth  = vt<int> (N);
        this->in     = vt<int> (N);
        this->out    = vt<int> (N);
        this->timer  = 0;
    }

    lca_tree(vt<T> val) {
        this->N      = val.size();
        this->L      = log2(N) + 2;
        this->par    = vt<int> (N, -1);
        this->val    = val;
        this->depth  = vt<int> (N);
        this->in     = vt<int> (N);
        this->out    = vt<int> (N);
        this->timer  = 0;
    }

    void dfs(int v, int p, vt<vt<int> > & g) {
        in[v] = ++timer;
        par[v] = p;
        if (p != -1) {
            depth[v] = depth[p] + 1;
        }

        for (int u : g[v]) {
            if (u == p) continue;
            dfs(u, v, g);
        }

        out[v] = timer;
    }

    void calc_dp_par() {
        dp_par = vt<vt<int> >(N, vt<int> (L, -1));

        for (int i = 0; i < N; i++) {
            dp_par[i][0] = i;
            dp_par[i][1] = par[i];
        }

        for (int l = 2; l < L; l++) {
            for (int i = 0; i < N; i++) {
                int par = dp_par[i][l - 1];
                if (par != -1) {
                    dp_par[i][l] = dp_par[par][l - 1];
                }
            }
        }
    }

    void calc_dp_val() {
        dp_val = vt<vt<T> >(N, vt<T> (L));

        for (int i = 0; i < N; i++) {
            dp_val[i][0] = val[i];
            dp_val[i][1] = val[i];
            if (par[i] != -1) {
                dp_val[i][1] = T::merge(dp_val[i][1], val[par[i]]);
            }
        }

        for (int l = 2; l < L; l++) {
            for (int i = 0; i < N; i++) {
                int par = dp_par[i][l - 1];
                if (par != -1) {
                    dp_val[i][l] = T::merge(dp_val[i][l - 1], dp_val[par][l - 1]);
                }
            }
        }
    }

    bool is_par(int v, int p) {
        return in[p] <= in[v] && out[v] <= out[p];
    }

    int lca(int node1, int node2) {
        if (is_par(node2, node1)) {
            return node1;
        }

        if (is_par(node1, node2)) {
            return node2;
        }

        for (int i = L - 1; i >= 1; i--) {
            int par = dp_par[node1][i];
            if (par == -1) continue;
            if (!is_par(node2, par)) {
                node1 = par;
            }
        }

        return par[node1];
    }

    // returns -1 if k-th parent doesn't exist 
    int kth_par(int node, int k) {
        if (k >= N) {
            return -1;
        }

        for (int i = L - 1; i >= 1; i--) {
            int par = dp_par[node][i];
            if (k & (1 << (i - 1))) {
                k ^= (1 << (i - 1));
                node = par;
                if (node == -1) {
                    return -1;
                }
            }
        }

        return node;
    }

    T ans(int node, int k) {
        if (k < 0) {
            return T();
        }
        T val = T();

        for (int i = L - 1; i >= 1; i--) {
            int par = dp_par[node][i];
            if (par == -1) continue;
            if (k & (1 << (i - 1))) {
                k ^= (1 << (i - 1));
                val = T::merge(val, dp_val[node][i]);
                node = par;
            }
        }

        return T::merge(val, dp_val[node][0]);
    }

    // to simulate edge - set value of directed edge (u -> v) to v-th node
    T ans_between_edges(int node1, int node2) {
        int p = lca(node1, node2);
        T ans1 = ans(node1, depth[node1] - depth[p] - 1),
          ans2 = ans(node2, depth[node2] - depth[p] - 1);

        return T::merge(ans1, ans2);
    }

    T ans_between_nodes(int node1, int node2) {
        int p = lca(node1, node2);
        T ans1 = ans(node1, depth[node1] - depth[p]),
          ans2 = ans(node2, depth[node2] - depth[p]);

        return T::merge(ans1, ans2);
    }
};

struct DSU {
    int n, cnt;
    vt<int> par, sz;

    DSU(int n) {
        this->n = n;
        cnt = n;
        par = vt<int> (n); iota(all(par), 0);
    }

    int find(int v) {
        if (par[v] == v) {
            return v;
        }

        return par[v] = find(par[v]);
    }

    bool merge(int v, int u) {
        v = find(v);
        u = find(u);
        if (u == v) {
            return false;
        }

        cnt--;
        par[v] = u;

        return true;
    }

    bool is_connected() {
        return cnt == n;
    }
};

void solve() {
    int n; cin >> n;
    vt<vt<int> > g(n);
    For(i, n - 1) {
        int u, v; cin >> u >> v; u--, v--;
        g[u].push_back(v);
        g[v].push_back(u);
    }
    vt<int> freq(n); read(freq);
    For(i, n) freq[i]--;
    vt<int> inorder;
    vt<int> visited(n);
    auto dfs = [&] (auto dfs, int u, int p) -> void {
        if (visited[u]) {
            assert(0 == 1);
        }
        visited[u] = 1;
        inorder.push_back(u);
        for (int v : g[u]) {
            if (v == p) continue;
            dfs(dfs, v, u);
        }
    };
    dfs(dfs, 0, -1);
    lca_tree<node> t = lca_tree<node>(n);
    t.dfs(0, -1, g);
    t.calc_dp_par();
    vt<int> freq_last(n, -1);
    DSU dsu = DSU(n);
    for (int i : inorder) {
        int last = freq_last[freq[i]];
        if (last != -1) {
            int cur = i;
            int p = t.lca(last, cur);
            while (t.depth[last] > t.depth[p]) {
                int par = dsu.find(t.par[last]);
                dsu.merge(last, par);
                last = par;
            }
            while (t.depth[cur] > t.depth[p]) {
                int par = dsu.find(t.par[cur]);
                dsu.merge(cur, par);
                cur = par;
            }
        }
        freq_last[freq[i]] = i;
    }
    cout << dsu.cnt - 1 << endl;
}

